import dayjs from "dayjs";
import dayOfYear from "dayjs/plugin/dayOfYear";
import { range } from "ramda";
// const range = (start, end, step = 1) => {
//     let output = [];

//     if (typeof end === 'undefined') {
//       end = start;
//       start = 0;
//     }

//     for (let i = start; i < end; i += step) {
//       output.push(i);
//     }

//     return output;
//   };

dayjs.extend(dayOfYear)


export function getRangeOfDates(firstDate, lastDate) {
    let s = dayjs(firstDate).dayOfYear()
    let e = dayjs(lastDate).dayOfYear()
    let startYear = dayjs(firstDate).year()

    let days = range(s, e) //should work between years now
    console.log("EventHelpers: days=" + range(s, e))
    const dates = days.map((day) => (
        dayjs(`${s > e ? startYear + 1 : startYear}-01-01`).dayOfYear(day)
    ))
    console.log("EventHelpers: " + dates)
    return dates
}
