import dayjs from "dayjs";
import dayOfYear from "dayjs/plugin/dayOfYear";
import { range } from "ramda";

dayjs.extend(dayOfYear)


export function getRangeOfDates(firstDate, lastDate) {
    const start = dayjs(firstDate).dayOfYear()
    const end = dayjs(lastDate).dayOfYear()
    const startYear = dayjs(firstDate).year()



    const days = range(start, end) //should work between years now
    console.log("EventHelpers: days=" + days)
    const dates = days.map((day) => (
        dayjs(`${start > end ? startYear + 1 : startYear}-01-01`).dayOfYear(day)
    ))
    console.log("EventHelpers: " + dates)
    return dates
}
